draw((0,1)--(-3,4),blue,EndArrow);
draw((-1,0)--(-4,3),blue,EndArrow);
draw((0,-1)--(-3,-4),blue,EndArrow);
draw((-1,0)--(-4,-3),blue,EndArrow);
draw((-5,0)--(5,0),EndArrow);
